mapper.select:
	select count(*)
	from board

        where board_category = #{value}

mapper.update:
update board set
	board_delete_yn = 'Y'
where board_no = #{boardNo}

update board set
	board_like = #{likeCnt}
where board_no = #{boardNo}

update board_comment set
	board_comment_del_flag = 'Y'
where board_comment_id = #{boardCommentId}
